import React from 'react';
import ReferralBanner from '../components/ReferralBanner';
// import './home.css';

const HomePage = () => {
  return (
    <div className="homepage">
      <header className="hero">
        <h1>Welcome to Lyms Microfinance</h1>
        <p>Your trusted partner in achieving financial freedom.</p>
        <a href="/loan-application" className="cta-btn">Apply for a Loan</a>
      </header>
      <section className="features">
        <h2>Why Choose Us?</h2>
        <div className="feature-cards">
          <div className="feature-card">
            <h3>Flexible Loan Options</h3>
            <p>Choose the loan type and duration that suits your needs.</p>
          </div>
          <div className="feature-card">
            <h3>Fast Approval</h3>
            <p>Get your loan approved and disbursed within 24 hours.</p>
          </div>
          <div className="feature-card">
            <h3>Low Interest Rates</h3>
            <p>Enjoy competitive interest rates tailored for you.</p>
          </div>
        </div>
      </section>
      <ReferralBanner />
    </div>
  );
};

export default HomePage;
