import React from 'react';
import { useFormik } from 'formik';
import * as Yup from 'yup';

const LoanApplication = () => {
  const formik = useFormik({
    initialValues: {
      fullName: '',
      loanType: '',
      amount: '',
      duration: '',
    },
    validationSchema: Yup.object({
      fullName: Yup.string().required('Full Name is required'),
      loanType: Yup.string().required('Select a loan type'),
      amount: Yup.number().required('Enter loan amount').min(500, 'Minimum is 500'),
      duration: Yup.number().required('Enter duration').min(1, 'Minimum is 1 week'),
    }),
    onSubmit: (values) => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  return (
    <div className="container my-5">
      <h2>Loan Application</h2>
      <form onSubmit={formik.handleSubmit}>
        <div className="mb-3">
          <label>Full Name</label>
          <input
            type="text"
            name="fullName"
            className="form-control"
            onChange={formik.handleChange}
            value={formik.values.fullName}
          />
          {formik.errors.fullName && <div className="text-danger">{formik.errors.fullName}</div>}
        </div>

        <div className="mb-3">
          <label>Loan Type</label>
          <select
            name="loanType"
            className="form-control"
            onChange={formik.handleChange}
            value={formik.values.loanType}
          >
            <option value="">Select Loan Type</option>
            <option value="salary">Salary Loan</option>
            <option value="business">Business Loan</option>
          </select>
          {formik.errors.loanType && <div className="text-danger">{formik.errors.loanType}</div>}
        </div>

        <div className="mb-3">
          <label>Loan Amount</label>
          <input
            type="number"
            name="amount"
            className="form-control"
            onChange={formik.handleChange}
            value={formik.values.amount}
          />
          {formik.errors.amount && <div className="text-danger">{formik.errors.amount}</div>}
        </div>

        <div className="mb-3">
          <label>Duration (weeks)</label>
          <input
            type="number"
            name="duration"
            className="form-control"
            onChange={formik.handleChange}
            value={formik.values.duration}
          />
          {formik.errors.duration && <div className="text-danger">{formik.errors.duration}</div>}
        </div>

        <button type="submit" className="btn btn-primary">Submit Application</button>
      </form>
    </div>
  );
};

export default LoanApplication;
